mapper.select:
SELECT
    ifnull(a.amount,0) todayAward,
ifnull(b.amount,0) riseAward,
    c.winLoss winLoss,
    c.bet_amount betAmount
FROM
    (
        SELECT
            SUM(ar.amount) amount
        FROM
            award_receive_record ar
                INNER JOIN `user` u ON ar.user_id = u.id
        WHERE
            1 = 1
          AND ar.award_type = 1

            AND u.first_proxy =#{proxyId}


            AND u.second_proxy =#{proxyId}


            AND u.third_proxy =#{proxyId}


            AND ar.create_time BETWEEN #{startTime} AND #{endTime}

    ) a,
    (
        SELECT
            SUM(ar.amount) amount
        FROM
            award_receive_record ar
                INNER JOIN `user` u ON ar.user_id = u.id
        WHERE
            1 = 1
          AND ar.award_type = 2

            AND u.first_proxy =#{proxyId}


            AND u.second_proxy =#{proxyId}


            AND u.third_proxy =#{proxyId}


            AND ar.receive_time BETWEEN #{startTime} AND #{endTime}

    ) b,
    (
        SELECT
            sum(valid_amount) bet_amount,
            sum(win_loss) winLoss
        FROM
            proxy_game_record_report gr
        WHERE
            1 = 1

            AND gr.first_proxy =#{proxyId}


            AND gr.second_proxy =#{proxyId}


            AND gr.third_proxy =#{proxyId}


            AND gr.order_times BETWEEN #{startDate} AND #{endDate}

    ) c

SELECT u.*, k.*, IFNULL(td.todayAward, 0.000) todayAward, IFNULL(rs.riseAward, 0.000) riseAward FROM ( SELECT account, id, `level`, create_time AS createTime FROM `user` WHERE 1 = 1
AND `level` IN
#{item}
AND `id` = #{userId}
) u LEFT JOIN ( SELECT user_id AS userId, SUM(betting_number) num, sum(bet_amount) betAmount, sum(valid_amount) validBet, sum(win_loss) winLoss FROM proxy_game_record_report gr
where order_times BETWEEN #{startDate} AND #{endDate}
GROUP BY user_id ) k ON k.userId = u.id LEFT JOIN ( SELECT user_id, SUM(amount) AS todayAward FROM award_receive_record WHERE 1 = 1 AND award_type = 1
AND create_time BETWEEN #{startTime} AND #{endTime}
GROUP BY user_id ) td ON u.id = td.user_id LEFT JOIN ( SELECT user_id, SUM(amount) AS riseAward FROM award_receive_record WHERE 1 = 1 AND award_type = 2
AND receive_time BETWEEN #{startTime} AND #{endTime}
GROUP BY user_id ) rs ON u.id = rs.user_id ORDER BY u.createTime DESC

SELECT a.validBet, a.winLoss, ifnull(b.todayAward,0) todayAward, ifnull(c.riseAward,0) riseAward FROM ( SELECT user_id, SUM(betting_number) num, sum(valid_amount) validBet, sum(win_loss) winLoss FROM proxy_game_record_report gr WHERE 1 = 1
and order_times BETWEEN #{startDate} AND #{endDate}
AND user_id IN ( SELECT id from `user` WHERE 1=1
AND `level` IN
#{item}
AND `id` = #{userId}
)
) a, ( SELECT SUM(amount) AS todayAward FROM award_receive_record WHERE award_type = 1
and create_time BETWEEN #{startTime} AND #{endTime}
AND user_id IN ( SELECT id from `user` WHERE 1=1
AND `level` IN
#{item}
AND `id` = #{userId}
)
) b, ( SELECT SUM(amount) AS riseAward FROM award_receive_record WHERE award_type = 2
AND receive_time BETWEEN #{startTime} AND #{endTime}
AND user_id IN ( SELECT id from `user` WHERE 1=1
AND `level` IN
#{item}
AND `id` = #{userId}
)
) c

SELECT ifnull(a.amount,0) todayAward, ifnull(b.amount,0) riseAward, c.winLoss winLoss, c.validbet validBet FROM ( SELECT SUM(ar.amount) amount FROM award_receive_record ar INNER JOIN `user` u ON ar.user_id = u.id WHERE 1 = 1 AND ar.award_type = 1
AND ( u.first_proxy IN ( SELECT id FROM proxy_user GROUP BY user_id ) or u.first_proxy is null)
AND u.first_proxy =#{proxyUserId}
AND u.second_proxy =#{proxyUserId}
AND u.third_proxy =#{proxyUserId}
AND ar.create_time BETWEEN #{startTime} AND #{endTime}
) a, ( SELECT SUM(ar.amount) amount FROM award_receive_record ar INNER JOIN `user` u ON ar.user_id = u.id WHERE 1 = 1 AND ar.award_type = 2
AND ( u.first_proxy IN ( SELECT id FROM proxy_user GROUP BY user_id ) or u.first_proxy is null)
AND u.first_proxy =#{proxyUserId}
AND u.second_proxy =#{proxyUserId}
AND u.third_proxy =#{proxyUserId}
AND ar.receive_time BETWEEN #{startTime} AND #{endTime}
) b, ( SELECT sum(win_loss) winLoss, sum(valid_amount) validbet FROM proxy_game_record_report gr WHERE 1 = 1
AND ( u.first_proxy IN ( SELECT first_proxy FROM proxy_game_record_report GROUP BY user_id ) or first_proxy is null)
AND gr.first_proxy =#{proxyUserId}
AND gr.second_proxy =#{proxyUserId}
AND gr.third_proxy =#{proxyUserId}
AND gr.order_times BETWEEN #{startDate} AND #{endDate}
) c
